package Entity.Immoveable;import Entity.Immoveable.RoadExtend.Spawn;import Entity.Immoveable.RoadExtend.Target;import core.Config;public class Road implements GameTile {    private double[][] roadInfo = new double[Config.MAP_HEIGHT][Config.MAP_WIDTH];    private Spawn spawn;    private Target target;    public Road(){}    public Road(int[][] roadTileMap){        setRoadInfo(roadTileMap);    }    public double[][] getRoadInfo() {        return roadInfo;    }    public void setSpawn(Spawn spawn) {        this.spawn = spawn;    }    public void setTarget(Target target) {        this.target = target;    }    public Spawn getSpawn() {        return spawn;    }    public Target getTarget() {        return target;    }    public double getRoadInfo(int i, int j){        if(i < 0 || i >= roadInfo.length) return Integer.MAX_VALUE;        if(j < 0 || j >= roadInfo[i].length) return Integer.MAX_VALUE;        return roadInfo[i][j];    }    public void setRoadInfo(int[][] roadTileMap) {        /*         * "0" is immovable and != 0 is movable         */        for (int i=0; i<Config.MAP_HEIGHT; i++){            for (int j=0; j<Config.MAP_WIDTH; j++){                if (roadTileMap[i][j] != 0) {                    roadInfo[i][j] = Math.sqrt(Math.pow((getTarget().getPosY()-i), 2) + Math.pow((getTarget().getPosX()-j), 2));                }                else roadInfo[i][j] = 0;            }        }    }    public void printTestRoadInfo(){        System.out.println("Road Information");        for (int i=0; i<Config.MAP_HEIGHT; i++){            for (int j=0; j<Config.MAP_WIDTH; j++){                System.out.print(roadInfo[i][j] + " ");            }            System.out.println();        }    }}