package Entity.Immoveable.RoadExtend;import Entity.Immoveable.Road;public class Target extends Road {    private static int info = 100;    private static int posX, posY;    public void setPosXY(int posX, int posY) {        Target.posX = posX;        Target.posY = posY;    }    public static int getPosX() {        return posX;    }    public static int getPosY() {        return posY;    }    @Override    public void setRoadInfo(int[][] roadTileMap) {        roadTileMap[posY][posX] = info;    }}