package Entity.Moveable.Enemy;import core.Config;import core.GameField;public class BossEnemy extends Enemy {    public BossEnemy() {        this.speed = Config.BOSS_ENEMY_SPEED;        this.scale = Config.BOSS_ENEMY_SIZE;        this.armor = Config.BOSS_ENEMY_ARMOR;        this.health = Config.BOSS_ENEMY_HEALTH;        this.reward = Config.BOSS_ENEMY_REWARD;        setImg(GameField.getImageSheet().imageSheet.get(248));        setFirstPos();    }}