package Entity.Moveable.Enemy;import core.Config;import core.GameField;public class SmallerEnemy extends Enemy {    public SmallerEnemy() {        this.speed = Config.SMALLER_ENEMY_SPEED;        this.scale = Config.SMALLER_ENEMY_SIZE;        this.armor = Config.SMALLER_ENEMY_ARMOR;        this.health = Config.SMALLER_ENEMY_HEALTH;        this.reward = Config.SMALLER_ENEMY_REWARD;        setImg(GameField.getImageSheet().imageSheet.get(246));        setFirstPos();    }    @Override    public double getMaxHealth() {        return Config.SMALLER_ENEMY_HEALTH;    }}