package Graphic;import core.Config;import core.GameField;import javafx.scene.Group;import javafx.scene.Scene;import javafx.scene.canvas.Canvas;import javafx.scene.image.Image;import javafx.scene.image.ImageView;import javafx.scene.layout.FlowPane;import javafx.stage.Stage;public class Render {    public Render(){ }    public static void renderMap(Stage primaryState, Group root){        ImageSheet imgsheet = new ImageSheet();        imgsheet.loadImageViewSheet("Graphic/towerDefense_tilesheet.png");        ImageView imageView = new ImageView();        int[][] map = GameField.background.getTileMap();        for (int i = 0; i < Config.SCREEN_HEIGHT; i++) {            for (int j = 0; j < Config.SCREEN_WIDTH; j++) {                imageView = new ImageView(imgsheet.imageSheet.get(map[0][0]));                imageView.setX(i*64);                imageView.setY(j*64);                root.getChildren().add(imageView);            }        }    }}