package core;import core.Config;/*|=======================================================================||   This class start a game field (game field's status, config, ...)    ||=======================================================================| */public class GameStage {    private static int playerHP = Config.GAME_HEART;    private static int playerFinance = Config.GAME_START_MONEY;    public GameStage (){}    public int getPlayerHP() {        return playerHP;    }    public void setPlayerHP(int playerHP) {        this.playerHP = playerHP;    }    public int getPlayerFinance() {        return playerFinance;    }    public void decreaseHP (){        setPlayerHP(getPlayerHP()-1);    }    public static void earnMoney(int money){        playerFinance += money;    }    public static void buyTower(int money) {        playerFinance -= money;    }    public static boolean hasEnoughMoney(int cost){        if (playerFinance >= cost) return true;        return false;    }    public boolean gameOver(){        if(getPlayerHP()<=0) return true;        return false;    }}